import Foundation
import NetworkKit

final class PokedexInteractor {
    weak var presenter: PokedexInteractorOutputInterface?
    let pokedexService: PokedexServiceProtocol = PokedexService()
}

// MARK: - Extensions -

extension PokedexInteractor: PokedexInteractorInterface {
    func getAllPokemon(offSet: Int) {
        pokedexService.getAllPokemon(offSet: offSet) { [weak self] response in
